import React, { useState } from 'react';
import '../styles/OurWorks.css';

const categories = ['All', 'Architectural', 'Interior', 'Structural'];

const works = [
  {
    id: 1,
    category: ['Architectural'],
    image: 'data:image/jpeg;base64,',
    title: 'SOME OF OUR ARCHITECTURAL WORK',
    details: `ARCHITECTURAL WORK PORTFOLIO :\nUpcoming SPA BUILDING at sch 140, near Pipliyahana, for mrs.Geeta Odiya. Plot size=30'x50'At scheme 94, near bombay hospital.[...]`,
    tags: ['Architectural', 'Interior', 'Structural']
  },
  {
    id: 2,
    category: ['Interior'],
    image: 'data:image/jpeg;base64,',
    title: 'INTERIOR DESIGN PROJECT',
    details: `INTERIOR DESIGN PORTFOLIO :\nModern office space with open layout, custom furniture, and ambient lighting. Focus on productivity and aesthetics.`,
    tags: ['Interior']
  },
  {
    id: 3,
    category: ['Structural'],
    image: 'data:image/jpeg;base64,',
    title: 'STRUCTURAL ENGINEERING PROJECT',
    details: `STRUCTURAL ENGINEERING PORTFOLIO :\nHigh-rise building with advanced seismic design and sustainable materials.`,
    tags: ['Structural']
  },
  // Add more works as needed
];

const OurWorks: React.FC = () => {
  const [activeTab, setActiveTab] = useState('All');
  const [hoveredId, setHoveredId] = useState<number | null>(null);

  const filteredWorks = activeTab === 'All' ? works : works.filter(w => w.category.includes(activeTab));

  return (
    <section className="our-works-section">
      <div className="our-works-header">
        <p className="our-works-subtitle">We Are Awesome</p>
        <h2 className="our-works-title">OUR WORKS</h2>
      </div>
      <div className="our-works-tabs">
        {categories.map(cat => (
          <button
            key={cat}
            className={`our-works-tab${activeTab === cat ? ' active' : ''}`}
            onClick={() => setActiveTab(cat)}
          >
            {cat}
          </button>
        ))}
      </div>
      <div className="our-works-grid">
        {filteredWorks.map(work => (
          <div
            key={work.id}
            className="our-works-card"
            onMouseEnter={() => setHoveredId(work.id)}
            onMouseLeave={() => setHoveredId(null)}
          >
            <img src={work.image} alt={work.title} className="our-works-image" />
            <div className={`our-works-details${hoveredId === work.id ? ' expanded' : ''}`}>
              <div className="our-works-tags">
                {work.tags.map(tag => (
                  <span key={tag} className="our-works-tag">{tag}</span>
                ))}
              </div>
              <h3 className="our-works-card-title">{work.title}</h3>
              <div className="our-works-card-desc">
                {work.details.split('\n').map((line, idx) => <p key={idx}>{line}</p>)}
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default OurWorks; 